<?phpif(!isset($_GET['path']) || $_GET['path'] == ""){	header("location:index.php?error=Veuillez indiquer un chemin");	return;}$maxDepth = 0;if(isset($_GET['maxDepth']))	$maxDepth = $_GET['maxDepth'];if(!is_numeric($maxDepth)){	echo "<font color = 'orange'>Nombre invalide, profondeur max = infini</font><br />";	$maxDepth = 0;}echo "<a href = 'index.php'>Retour</a><br /><br />";$path = $_GET['path'];if(@opendir($path) === FALSE){	header("location:index.php?error=Chemin invalide");	return;}?>