<html><head>	<title>Liste fichiers</title>	<script type="text/javascript">			function showDiv(divName)			{					//alert("salut");				var div = document.getElementById(divName);				div.style.display = '';			}			function hideDiv(divName)			{					//alert("salut");				var div = document.getElementById(divName);				div.style.display = 'none';			}	</script></head><body><?phpinclude("loadVar.php");$id = 0;printAllFiles($path, 0, $maxDepth, &$id);function printAllFiles($path, $depth, $maxDepth, &$id){	$depth++;	$id++;		$lastSlash = strrpos($path, '/');	if($lastSlash <= 0)		$lastSlash = -1;	$folderName = substr($path, $lastSlash+1, strlen($path)-$lastSlash-1);		if($depth > 0)		echo "<div id = '".($id+0)."'>";		//echo "<ul id = '".($id+0)."'>";	echo "<ul>";		//echo "<ul id = '".$depth."'>";	//echo "<li><b>$folderName</b></li>";		echo "<a onclick='hideDiv(".($id+1).")'><li><b>$folderName</b></li></a>";	if($maxDepth != 0 && $depth > $maxDepth)	{		echo "</ul></div>";		return;	}	$dir = @opendir($path);	if($dir === FALSE)	{		echo "<font color = 'orange'>Impossible de lire '".$path."'</font><br />";		continue;	}	$dirs = array();	$str = "";	while($file = readdir($dir))	{		if($file == '.' || $file == '..')			continue;		if(is_dir($path.'/'.$file))			$dirs[] = $path.'/'.$file;		else		{			$str .= "<li>".$file."</li>";			//echo "<li>".$file."</li>";		}	}	//if($str != "")		//echo "<ul>".$str."</ul>";	asort($dirs);	foreach($dirs as $onedir)		printAllFiles($onedir, $depth, $maxDepth, &$id);	closedir($dir);	echo "</ul>";}?></body></html>